import { Component, OnInit } from '@angular/core';
import { ApiService } from '../../api.service';
import { Theme } from '../../types/theme';
import { UserService } from 'src/app/user/user.service';

@Component({
  selector: 'app-themes-list',
  templateUrl: './themes-list.component.html',
  styleUrls: ['./themes-list.component.css'],
})
export class ThemesListComponent implements OnInit {
  themes: Theme[] | null = [];
  isLoading: boolean = true;

  constructor(private api: ApiService, private userService: UserService) { }

  get isLoggedIn(): boolean {
    return this.userService.isLogged;
  }

  get userId(): string {
    return this.userService.user?.id || '';
  }

  ngOnInit(): void {
    this.api.getThemes().subscribe((themes) => {
      this.themes = themes;

      setTimeout(() => {
        this.isLoading = false;
      }, 1000);
    });
  }

  isSubscribed(theme: Theme) {
    const isSubscribedUser = theme.subscribers.find((s) => {

      return s === this.userService.user?.id;
    });

    return !!isSubscribedUser;
  }
}
<main>
  <app-welcome-msg *ngIf="!isLoggedIn" [isLoggedIn]="isLoggedIn"/>
  <div *ngIf="isLoading">
    <app-loader />
  </div>

  <div *ngIf="!isLoading" class="theme-title">
    <!-- theme component  -->
    <div *ngFor="let theme of themes" class="theme-container">
      <div class="theme-name-wrapper">
        <div class="theme-name">
          <a [routerLink]="theme._id" class="normal">
            <h2>{{ theme.themeName | slice }}</h2>
          </a>
          <div class="columns">
            <div>
              <p>
                Date: <time>{{ theme.created_at }}</time>
              </p>
              <div class="nick-name">
                <p>
                  Username: <span>{{ theme.userId.username }}</span>
                </p>
              </div>
            </div>
            <div class="subscribers">
              <button *ngIf="isLoggedIn && !isSubscribed(theme)" class="subscribe">Subscribe</button>
              <button *ngIf="isLoggedIn && isSubscribed(theme)" class="unsubscribe">Unsubscribe</button>
              <p>
                Subscribers: <span>{{ theme.subscribers.length || 0 }}</span>
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</main>
