  providedIn: 'root'
})
export class ErrorService {
  apiErrror$$ = new BehaviorSubject(null);
  apiError$ = this.errorService.apiErrror$$.asObservable();

  constructor() { }

  setError(error: any): void {
    this.apiErrror$$.next(error);
  }
}
