import { Component, OnInit } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { EMAIL_DOMAINS } from 'src/app/constants';
import { emailValidator } from 'src/app/shared/utils/email-validator';
import { ProfileDetails } from 'src/app/types/user';
import { UserService } from '../user.service';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent implements OnInit{
  showEditMode: boolean = false;

  profileDetails: ProfileDetails = {
    username: '',
    email: '',
    tel: ''
  }

  form = this.fb.group({
    username: ['', [Validators.required, Validators.minLength(5)]],
    email: ['', [Validators.required, emailValidator(EMAIL_DOMAINS)]],
    tel: [''],
  })

  constructor(private fb: FormBuilder, private userService: UserService) { }

  on

  onToggle(): void {
    this.showEditMode = !this.showEditMode;
  }

  saveProfileHandler(): void {
    if (this.form.invalid) {
      return;
    }

    this.profileDetails = this.form.value as ProfileDetails;
    const { username, email, tel } = this.profileDetails;

    this.userService.updateProfile(username, email, tel).subscribe(() => {
      this.onToggle();
    });
  }

  onCancel(e: Event) {
    e.preventDefault();
    this.onToggle();
  }
}
<!-- homepage -->
<div class="container">

    <div class="profile"><img src="../../../assets/profile.png" alt="default user">
        <h3>User Info:</h3>
        <!-- READONLY MODE -->
        <ng-container *ngIf="!showEditMode">
            <div class="flex">
                <p>Username: </p>
                <p>{{profileDetails.username}}</p>
            </div>
            <div class="flex">
                <p>Email: </p>
                <p>{{profileDetails.email}}</p>
            </div>
            <div class="flex">
                <p>Phone: </p>
                <p>+359 {{profileDetails.tel}}</p>
            </div>
            <button class="edit-button" (click)="onToggle()">Edit</button>
        </ng-container>
        <!-- EDIT MODE -->   
        <ng-container *ngIf="showEditMode">
        <form [formGroup]="form" (ngSubmit)="saveProfileHandler()">
            <!-- username -->
            <div class="flex">
                <p>Username: </p>
                <input type="text" name="username" id="username" [value]="profileDetails.username"
                formControlName="username"
                >
            </div>

            <!-- username validations -->
            <ng-container *ngIf="form.get('username')?.touched">
                <p class="error" *ngIf="form.get('username')?.errors?.['required']">Username is required!</p>
                <p class="error" *ngIf="form.get('username')?.errors?.['minlength']">Username must be at least 5 characters!</p>
            </ng-container>

            <!-- email -->
            <div class="flex">
                <p>Email: </p>
                <input type="email" name="email" id="email" [value]="profileDetails.email"
                formControlName="email"
                >
            </div>

             <!-- email validations -->
             <ng-container *ngIf="form.get('email')?.touched">
                <p class="error" *ngIf="form.get('email')?.errors?.['required']">Email is required!</p>
                <p class="error" *ngIf="form.get('email')?.errors?.['emailValidator']">Email is not valid!</p>
            </ng-container>

            <!-- tel -->
            <div class="flex">
                <p>Phone: </p>
                <div>
                    <select name="select-tel" id="select-tel" class="tel">
                        <option value="">+359</option>
                    </select>
                    <input type="text" name="tel" id="tel" [value]="profileDetails.tel"
                    formControlName="tel"
                    >
                </div>
            </div>

            <!-- actions -->
            <button class="red-button" (click)="onCancel($event)">Cancel</button>
            <button class="green-button"
            [disabled]="form.invalid"
            [style]="{backgroundColor: form.invalid ? 'grey' : 'green'}"
            >Save</button>
        </form>
        </ng-container>
    </div>
</div>
